Map::Map() :
	m_CollLeft(false),
	m_CollRight(false),
	m_handle(-1),
	m_StageNum(1),
	m_graphWidth(0),
	m_graphHeight(0),
	m_LeftNum(0),
	m_RightNum(0),
	m_Pos(0, 0),
	m_ScrollSpeed(0, 0),
	m_Vec(0,0)
{
	for (int i = 0; i < kBgNumY; i++)
	{
		for (int j = 0; j < kBgNumX; j++)
		{
			m_MapData[i][j] = kMapData1[i][j];
		}
	}

	
}

Map::~Map()
{
}

void Map::load()
{
	m_handle = LoadGraph(kFileName);
	GetGraphSize(m_handle, &m_graphWidth, &m_graphHeight);
}

void Map::unload()
{
	DeleteGraph(m_handle);
}

void Map::update(Vec2 offset)
{
	m_Pos.x = offset.x;

	//assert(m_StageNum != 3);

	switch (m_StageNum)
	{
	case 1:
		for (int y = 0; y < kBgNumY; y++)
		{
			for (int x = 0; x < kBgNumX; x++)
			{
				m_MapData[y][x] = kMapData1[y][x];
			}
		}
		break;

	case 2:
		for (int y = 0; y < kBgNumY; y++)
		{
			for (int x = 0; x < kBgNumX; x++)
			{
				m_MapData[y][x] = kMapData2[y][x];
			}
		}
		break;

	case 3:
		for (int y = 0; y < kBgNumY; y++)
		{
			for (int x = 0; x < kBgNumX; x++)
			{
				m_MapData[y][x] = kMapData3[y][x];
			}
		}
		break;

	case 4:
		for (int y = 0; y < kBgNumY; y++)
		{
			for (int x = 0; x < kBgNumX; x++)
			{
				m_MapData[y][x] = kMapData4[y][x];
			}
		}
		break;

	case 5:
		for (int y = 0; y < kBgNumY; y++)
		{
			for (int x = 0; x < kBgNumX; x++)
			{
				m_MapData[y][x] = kMapData5[y][x];
			}
		}
		break;
	}
	
}

void Map::draw()
{
	drawMap();
}

void Map::PlayerMove(float knockback, float Move)
{
	m_Vec.x -= knockback;
	m_Vec.x += Move;
}

void Map::SetStage()
{
	if (m_StageNum < 5)
	{
		m_StageNum++;
	}
}

int Map::getWidth() const
{
	return kChipSize * kBgNumX;
}

int Map::getHeight() const
{
	return kChipSize * kBgNumY;
}

int Map::chipNumX()
{
	return (m_graphWidth / kChipSize);
}

int Map::chipNumY()
{
	return (m_graphHeight / kChipSize);
}

int Map::chipNum()
{
	return(chipNumX() * chipNumY());
}

void Map::drawMap()
{
	for (int x = 0; x < kBgNumX; x++)
	{
		for (int y = 0; y < kBgNumY; y++)
		{
			int ChipNumY = m_MapData[y][x] / kChipDivisionY;
			int ChipNumX = m_MapData[y][x] % kChipDivisionY;

			DrawRectGraph(m_Pos.x + kChipSize * x, m_Pos.y + kChipSize * y,
				kChipSize * ChipNumX, kChipSize * ChipNumY,
				kChipSize, kChipSize, m_handle, true);
		}
	}
}
